/* jshint esversion: 8 */
// Distance Between Rippa and Eon – Animated Calculation
// - Both profiles (Eon and Rippa) are movable
// - Click CALCULATE to see an animated subtraction measurement
// - Bars from 0→A and 0→B, overlap cancels, remainder = distance
// - "NEW POSITIONS" gives random positions for both

// ==============================
// Config
// ==============================
const ORIG_W = 900, ORIG_H = 740;
const minValue = -20;
const maxValue = 20;
const tickSpacing = 1;
const labelInterval = 5;
const personHitRadius = 40;

// ==============================
// State
// ==============================
let initialized = false;
let axisY;
let axisStartX;
let axisEndX;

let personAValue = -5;  // Eon
let personBValue = 7;   // Rippa
let dragTarget = null;  // "A" | "B" | null
let isDragging = false;

// Animation state
let animState = "idle"; // "idle" | "step1" | "step2" | "step3" | "step4"
let animStepStart = 0;  // millis() when current step began
const STEP_MS = { step1: 1200, step2: 1200, step3: 2200, step4: 999999 };

// Buttons
const newDistBtn = { x: 0, y: 0, w: 160, h: 40 };
const calcBtn    = { x: 0, y: 0, w: 180, h: 44 };
const resetBtn   = { x: 0, y: 0, w: 140, h: 38 };

let imgKid, imgMe;
const profileRadius = 30;
const profileOutlineWidth = 3;
const arrowToAxisLength = 26;
const gapAboveProfiles = 20;
const labelAboveLineGap = 14;
const labelPersonA = "Eon";
const labelPersonB = "Rippa";

// Bar visual config – generous spacing to avoid overlap
const barH = 16;
let barRow1Offset, barRow2Offset, resultBarOffset;

// Inline fallbacks for p5canvas / when assets-data.js is not loaded
// (kid.png = Eon, me.png = Rippa)
// ASSET_FALLBACKS_START
const ASSET_ME_DATA_FALLBACK = "data:image/png;base64,";
const ASSET_KID_DATA_FALLBACK = "data:image/png;base64,";
// ASSET_FALLBACKS_END

// ==============================
// Easing
// ==============================
function easeInOut(t) {
  return t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;
}

// ==============================
// Setup
// ==============================
async function setup() {
  createCanvas(Math.min(ORIG_W, windowWidth), Math.min(ORIG_H, windowHeight));
  textFont("Arial");
  textAlign(CENTER, CENTER);
  updateLayout();

  var meData = ASSET_ME_DATA_FALLBACK;
  var kidData = ASSET_KID_DATA_FALLBACK;
  if (typeof window !== "undefined" && window.ASSET_ME_DATA) meData = window.ASSET_ME_DATA;
  if (typeof window !== "undefined" && window.ASSET_KID_DATA) kidData = window.ASSET_KID_DATA;

  var base = "";
  try { base = new URL("./", window.location.href).href; } catch (e) {}

  imgMe = await loadImage(meData || (base + "assets/me.png"));
  imgKid = await loadImage(kidData || (base + "assets/kid.png"));

  newRound();
  initialized = true;
}

// ==============================
// Draw
// ==============================
function draw() {
  if (!initialized) {
    if (typeof createCanvas === "function") createCanvas(Math.min(ORIG_W, windowWidth), Math.min(ORIG_H, windowHeight));
    if (typeof textFont === "function") textFont("Arial");
    if (typeof textAlign === "function") textAlign(CENTER, CENTER);
    updateLayout();
    newRound();
    initialized = true;
  } else {
    updateLayout();
  }

  background(255);

  drawInstructions();
  drawNumberLine();
  drawPersonA();
  drawPersonB();
  drawDistanceBracket();

  if (animState === "idle") {
    drawIdleFeedback();
    drawCalculateButton();
  } else {
    updateAnimStep();
    drawAnimationVisual();
    if (animState === "step4") drawResetButton();
  }

  drawNewDistanceButton();
}

function updateLayout() {
  axisY = height * (210 / 740);
  const marginX = Math.min(80, width * 0.09);
  axisStartX = marginX;
  axisEndX = width - marginX;

  barRow1Offset = Math.round(height * 0.105);
  barRow2Offset = Math.round(height * 0.181);
  resultBarOffset = Math.round(height * 0.335);

  newDistBtn.x = width - newDistBtn.w - 24;
  newDistBtn.y = height - 52;
  calcBtn.x = (width - calcBtn.w) / 2;
  calcBtn.y = height - 56;
  resetBtn.x = (width - resetBtn.w) / 2;
  resetBtn.y = height - 52;
}

function windowResized() {
  resizeCanvas(Math.min(ORIG_W, windowWidth), Math.min(ORIG_H, windowHeight));
  updateLayout();
}

// ==============================
// Animation state machine
// ==============================
function startAnimation() {
  if (personAValue === personBValue) return; // no animation for same position
  animState = "step1";
  animStepStart = millis();
}

function updateAnimStep() {
  if (animState === "idle" || animState === "step4") return;
  var elapsed = millis() - animStepStart;
  var duration = STEP_MS[animState] || 1200;
  if (elapsed >= duration) {
    // Advance
    if (animState === "step1") { animState = "step2"; animStepStart = millis(); }
    else if (animState === "step2") { animState = "step3"; animStepStart = millis(); }
    else if (animState === "step3") { animState = "step4"; animStepStart = millis(); }
  }
}

function resetAnimation() {
  animState = "idle";
  animStepStart = 0;
}

function stepProgress() {
  if (animState === "idle") return 1;
  var elapsed = millis() - animStepStart;
  var duration = STEP_MS[animState] || 1200;
  return constrain(elapsed / duration, 0, 1);
}

// ==============================
// Instructions
// ==============================
function drawInstructions() {
  noStroke();
  fill(40);
  textSize(20);
  textStyle(BOLD);
  text("Distance Between Rippa and Eon \u2013 Calculation", width / 2, 28);
  textStyle(NORMAL);
  textSize(14);
  fill(80);
  if (animState === "idle") {
    text("Drag either person, then click CALCULATE to see an animated measurement.", width / 2, 52);
  } else {
    text("Watch how we measure the distance by subtracting positions from 0.", width / 2, 52);
  }
}

// ==============================
// Number line
// ==============================
function drawNumberLine() {
  stroke(40);
  strokeWeight(3);
  line(axisStartX, axisY, axisEndX, axisY);
  drawArrowHead(axisStartX, axisY, -1);
  drawArrowHead(axisEndX, axisY, 1);

  for (let v = minValue; v <= maxValue; v++) {
    const x = valueToX(v);
    const isLabel = v % labelInterval === 0;
    stroke(30);
    strokeWeight(isLabel ? 3 : 2);
    const h = isLabel ? 18 : 10;
    line(x, axisY - h / 2, x, axisY + h);
    if (isLabel) {
      noStroke();
      fill(60);
      textSize(13);
      text(v, x, axisY + 32);
    }
  }
}

function drawArrowHead(x, y, dir) {
  push();
  translate(x, y);
  noStroke();
  fill(40);
  const size = 10;
  triangle(dir * 0, 0, dir * -size, -size / 1.2, dir * -size, size / 1.2);
  pop();
}

// ==============================
// Person B (Rippa) – movable
// ==============================
function drawPersonB() {
  const px = valueToX(personBValue);
  const centerY = axisY - profileRadius - arrowToAxisLength - 6;

  if (dragTarget === "B") {
    noFill(); stroke(80, 100, 160); strokeWeight(3);
    ellipse(px, centerY, profileRadius * 2 + 10, profileRadius * 2 + 10);
  }
  drawProfileWithArrow(px, centerY, imgMe);
  const labelTopY = centerY - profileRadius - gapAboveProfiles - labelAboveLineGap;
  noStroke(); fill(40); textSize(13);
  text(personBValue, px, labelTopY);
  fill(60); textSize(13);
  text(labelPersonB, px, axisY + 48);
}

// ==============================
// Person A (Eon) – movable
// ==============================
function drawPersonA() {
  const px = valueToX(personAValue);
  const centerY = axisY - profileRadius - arrowToAxisLength - 6;

  if (dragTarget === "A") {
    noFill(); stroke(140, 80, 180); strokeWeight(3);
    ellipse(px, centerY, profileRadius * 2 + 10, profileRadius * 2 + 10);
  }
  drawProfileWithArrow(px, centerY, imgKid);
  const labelTopY = centerY - profileRadius - gapAboveProfiles - labelAboveLineGap;
  fill(60, 40, 120); textSize(13);
  text(personAValue, px, labelTopY);
  fill(60); textSize(13);
  text(labelPersonA, px, axisY + 48);
}

function drawProfileWithArrow(centerX, centerY, img) {
  const r = profileRadius;
  if (img && img.width > 0) {
    push(); translate(centerX, centerY);
    drawingContext.save();
    drawingContext.beginPath();
    drawingContext.arc(0, 0, r, 0, TWO_PI);
    drawingContext.clip();
    imageMode(CENTER);
    image(img, 0, 0, r * 2, r * 2);
    drawingContext.restore();
    pop();
  } else {
    fill(200); noStroke();
    ellipse(centerX, centerY, r * 2, r * 2);
  }
  noFill(); stroke(60, 60, 80); strokeWeight(profileOutlineWidth);
  ellipse(centerX, centerY, r * 2, r * 2);
  noStroke();
  const arrowTopY = centerY + r;
  const arrowTipY = axisY - 6;
  stroke(50); strokeWeight(2);
  line(centerX, arrowTopY, centerX, arrowTipY);
  noStroke(); fill(50);
  triangle(centerX - 5, arrowTipY, centerX + 5, arrowTipY, centerX, axisY);
}

// ==============================
// Distance bracket (always visible above profiles)
// ==============================
function drawDistanceBracket() {
  const distUnits = Math.abs(personAValue - personBValue);
  if (distUnits === 0) return;

  const profileCenterY = axisY - profileRadius - arrowToAxisLength - 6;
  const personAX = valueToX(personAValue);
  const personBX = valueToX(personBValue);
  const distanceLineY = profileCenterY - profileRadius - gapAboveProfiles;
  const leftX = Math.min(personAX, personBX);
  const rightX = Math.max(personAX, personBX);
  const profileTopY = profileCenterY - profileRadius;

  stroke(34, 100, 50, 120); strokeWeight(1.5);
  line(personAX, profileTopY, personAX, distanceLineY);
  line(personBX, profileTopY, personBX, distanceLineY);

  stroke(34, 100, 50); strokeWeight(3);
  line(leftX, distanceLineY, rightX, distanceLineY);
  noStroke(); fill(34, 100, 50);
  const arrowTowardB = personAValue < personBValue;
  if (arrowTowardB) {
    triangle(rightX - 10, distanceLineY, rightX, distanceLineY - 6, rightX, distanceLineY + 6);
  } else {
    triangle(leftX + 10, distanceLineY, leftX, distanceLineY - 6, leftX, distanceLineY + 6);
  }
  noStroke(); fill(20, 80, 40); textSize(15); textStyle(BOLD);
  text(distUnits, (leftX + rightX) / 2, distanceLineY - 12);
  textStyle(NORMAL);
}

// ==============================
// Idle feedback text
// ==============================
function drawIdleFeedback() {
  const distUnits = Math.abs(personAValue - personBValue);
  noStroke(); fill(50); textSize(15);
  const msgY = axisY + 80;
  if (distUnits === 0) {
    fill(40, 120, 60); textSize(18); textStyle(BOLD);
    text("Same position! Distance is 0.", width / 2, msgY);
    textStyle(NORMAL);
  } else {
    text("Distance = | " + personAValue + " \u2212 " + (personBValue < 0 ? "(" + personBValue + ")" : personBValue) + " | = " + distUnits + " units", width / 2, msgY);
    fill(100); textSize(13);
    text("Click CALCULATE below to see the animated measurement!", width / 2, msgY + 26);
  }
}

// ==============================
// Animation visual – bars + overlap + result
// ==============================
function drawAnimationVisual() {
  const A = personAValue;
  const B = personBValue;
  const x0 = valueToX(0);
  const xA = valueToX(A);
  const xB = valueToX(B);
  const lo = Math.min(A, B);
  const hi = Math.max(A, B);
  const xLo = valueToX(lo);
  const xHi = valueToX(hi);
  const distUnits = hi - lo;

  // Bar intervals (pixel space)
  const barA_left = Math.min(x0, xA);
  const barA_right = Math.max(x0, xA);
  const barB_left = Math.min(x0, xB);
  const barB_right = Math.max(x0, xB);

  // Overlap
  const oLeft = Math.max(barA_left, barB_left);
  const oRight = Math.min(barA_right, barB_right);
  const hasOverlap = oRight > oLeft;

  const t = easeInOut(stepProgress());

  const bar1Y = axisY + barRow1Offset;
  const bar2Y = axisY + barRow2Offset;
  const resY = axisY + resultBarOffset;

  // Draw reference line at 0
  stroke(150); strokeWeight(1);
  drawingContext.setLineDash([4, 4]);
  line(x0, axisY + 8, x0, resY + barH + 4);
  drawingContext.setLineDash([]);

  // ---- Step 1: Bar from 0 to A ----
  if (animState === "step1" || animState === "step2" || animState === "step3" || animState === "step4") {
    var p1 = (animState === "step1") ? t : 1;
    var curXA = lerp(x0, xA, p1);
    var bL = Math.min(x0, curXA);
    var bW = Math.abs(curXA - x0);

    // In step3/4, gray out overlap
    var barAlpha = 220;
    if ((animState === "step3" || animState === "step4") && hasOverlap) barAlpha = 100;

    noStroke(); fill(230, 130, 50, barAlpha);
    rect(bL, bar1Y, bW, barH, 3);

    // Label
    noStroke(); fill(180, 100, 30); textSize(12); textStyle(BOLD);
    var labelX1 = (animState === "step1") ? curXA : xA;
    text(labelPersonA + " = " + A, (x0 + labelX1) / 2, bar1Y - 10);
    textStyle(NORMAL);
  }

  // ---- Step 2: Bar from 0 to B ----
  if (animState === "step2" || animState === "step3" || animState === "step4") {
    var p2 = (animState === "step2") ? t : 1;
    var curXB = lerp(x0, xB, p2);
    var bL2 = Math.min(x0, curXB);
    var bW2 = Math.abs(curXB - x0);

    var barAlpha2 = 220;
    if ((animState === "step3" || animState === "step4") && hasOverlap) barAlpha2 = 100;

    noStroke(); fill(50, 100, 180, barAlpha2);
    rect(bL2, bar2Y, bW2, barH, 3);

    noStroke(); fill(30, 70, 150); textSize(12); textStyle(BOLD);
    var labelX2 = (animState === "step2") ? curXB : xB;
    text(labelPersonB + " = " + B, (x0 + labelX2) / 2, bar2Y - 10);
    textStyle(NORMAL);
  }

  // ---- Step 3: Cancel overlap + highlight remaining ----
  if (animState === "step3" || animState === "step4") {
    var p3 = (animState === "step3") ? t : 1;

    if (hasOverlap) {
      // Draw striped overlay on overlap region for both bars
      var overlapAlpha = Math.round(lerp(0, 200, p3));
      noStroke(); fill(220, 220, 220, overlapAlpha);
      rect(oLeft, bar1Y, oRight - oLeft, barH, 2);
      rect(oLeft, bar2Y, oRight - oLeft, barH, 2);

      // Cross-hatch lines on overlap – clipped to overlap region
      stroke(200, 80, 80, overlapAlpha); strokeWeight(1.5);
      var oW = oRight - oLeft;

      // Clip and draw hatching for bar 1
      drawingContext.save();
      drawingContext.beginPath();
      drawingContext.rect(oLeft, bar1Y, oW, barH);
      drawingContext.clip();
      for (var cx = oLeft - barH; cx < oRight; cx += 8) {
        line(cx, bar1Y, cx + barH, bar1Y + barH);
      }
      drawingContext.restore();

      // Clip and draw hatching for bar 2
      drawingContext.save();
      drawingContext.beginPath();
      drawingContext.rect(oLeft, bar2Y, oW, barH);
      drawingContext.clip();
      for (var cx2 = oLeft - barH; cx2 < oRight; cx2 += 8) {
        line(cx2, bar2Y, cx2 + barH, bar2Y + barH);
      }
      drawingContext.restore();
    }

    // Highlight the remaining (non-overlap) parts in green
    var greenAlpha = Math.round(lerp(0, 220, p3));
    noStroke();

    // Remaining from bar A (the part not overlapped)
    if (barA_right > oRight && hasOverlap) {
      fill(34, 160, 60, greenAlpha);
      rect(oRight, bar1Y, barA_right - oRight, barH, 2);
    }
    if (barA_left < oLeft && hasOverlap) {
      fill(34, 160, 60, greenAlpha);
      rect(barA_left, bar1Y, oLeft - barA_left, barH, 2);
    }
    // Remaining from bar B
    if (barB_right > oRight && hasOverlap) {
      fill(34, 160, 60, greenAlpha);
      rect(oRight, bar2Y, barB_right - oRight, barH, 2);
    }
    if (barB_left < oLeft && hasOverlap) {
      fill(34, 160, 60, greenAlpha);
      rect(barB_left, bar2Y, oLeft - barB_left, barH, 2);
    }

    // If no overlap (opposite sides of 0), both full bars are "remaining"
    if (!hasOverlap) {
      fill(34, 160, 60, greenAlpha);
      rect(barA_left, bar1Y, barA_right - barA_left, barH, 2);
      fill(34, 160, 60, greenAlpha);
      rect(barB_left, bar2Y, barB_right - barB_left, barH, 2);
    }

    // Label
    noStroke();
    fill(120, 40, 40, Math.round(lerp(0, 255, p3)));
    textSize(13);
    if (hasOverlap) {
      text("Common part cancels out!", width / 2, bar2Y + barH + 34);
    } else {
      text("No overlap \u2013 bars go opposite directions. Distance = sum of lengths!", width / 2, bar2Y + barH + 34);
    }
  }

  // ---- Step 4: Result bar + equation ----
  if (animState === "step4") {
    var p4 = easeInOut(constrain((millis() - animStepStart) / 800, 0, 1));

    // Result bar from lo to hi
    var rW = (xHi - xLo) * p4;
    noStroke(); fill(34, 160, 60, 230);
    rect(xLo, resY, rW, barH, 4);

    // Result label
    noStroke(); fill(20, 100, 40); textSize(14); textStyle(BOLD);
    text("Distance = " + distUnits, (xLo + xHi) / 2, resY - 10);
    textStyle(NORMAL);

    // Connecting dashed lines
    stroke(34, 160, 60, 150); strokeWeight(1);
    drawingContext.setLineDash([3, 3]);
    line(xLo, axisY + 6, xLo, resY);
    line(xHi, axisY + 6, xHi, resY);
    drawingContext.setLineDash([]);

    // Equation (fades in after bar)
    var eqAlpha = Math.round(lerp(0, 255, constrain((millis() - animStepStart - 600) / 600, 0, 1)));
    noStroke(); textSize(15);
    fill(40, 40, 40, eqAlpha);
    var eqY = resY + barH + 26;
    text("| " + labelPersonA + " \u2212 " + labelPersonB + " |  =  | " + A + " \u2212 " + (B < 0 ? "(" + B + ")" : B) + " |  =  | " + (A - B) + " |  =  " + distUnits, width / 2, eqY);

    // Celebration text
    var cel = Math.round(lerp(0, 255, constrain((millis() - animStepStart - 1200) / 500, 0, 1)));
    fill(34, 130, 60, cel); textSize(14); textStyle(BOLD);
    text("\u2714  The remaining part after subtraction = the distance!", width / 2, eqY + 26);
    textStyle(NORMAL);
  }
}

// ==============================
// CALCULATE button
// ==============================
function drawCalculateButton() {
  const distUnits = Math.abs(personAValue - personBValue);
  if (distUnits === 0) return;

  const hover =
    mouseX >= calcBtn.x && mouseX <= calcBtn.x + calcBtn.w &&
    mouseY >= calcBtn.y && mouseY <= calcBtn.y + calcBtn.h;

  stroke(30, 120, 60); strokeWeight(2);
  fill(hover ? color(180, 240, 200) : color(210, 245, 220));
  rect(calcBtn.x, calcBtn.y, calcBtn.w, calcBtn.h, 10);
  noStroke(); fill(20, 100, 40); textSize(17); textStyle(BOLD);
  text("\u25B6  CALCULATE", calcBtn.x + calcBtn.w / 2, calcBtn.y + calcBtn.h / 2);
  textStyle(NORMAL);
}

// ==============================
// RESET button
// ==============================
function drawResetButton() {
  const hover =
    mouseX >= resetBtn.x && mouseX <= resetBtn.x + resetBtn.w &&
    mouseY >= resetBtn.y && mouseY <= resetBtn.y + resetBtn.h;

  stroke(100, 60, 40); strokeWeight(2);
  fill(hover ? color(240, 215, 195) : color(250, 235, 220));
  rect(resetBtn.x, resetBtn.y, resetBtn.w, resetBtn.h, 8);
  noStroke(); fill(120, 60, 20); textSize(15); textStyle(BOLD);
  text("\u21A9  TRY AGAIN", resetBtn.x + resetBtn.w / 2, resetBtn.y + resetBtn.h / 2);
  textStyle(NORMAL);
}

// ==============================
// NEW POSITIONS button
// ==============================
function drawNewDistanceButton() {
  const hover =
    mouseX >= newDistBtn.x && mouseX <= newDistBtn.x + newDistBtn.w &&
    mouseY >= newDistBtn.y && mouseY <= newDistBtn.y + newDistBtn.h;

  stroke(80, 40, 120); strokeWeight(2);
  fill(hover ? 200 : 230, 220, 245);
  rect(newDistBtn.x, newDistBtn.y, newDistBtn.w, newDistBtn.h, 8);
  noStroke(); fill(80, 40, 120); textSize(15); textStyle(BOLD);
  text("NEW POSITIONS", newDistBtn.x + newDistBtn.w / 2, newDistBtn.y + newDistBtn.h / 2);
  textStyle(NORMAL);
}

// ==============================
// New round
// ==============================
function newRound() {
  personAValue = floor(random(minValue, maxValue + 1));
  personBValue = floor(random(minValue, maxValue + 1));
  personAValue = constrain(personAValue, minValue, maxValue);
  personBValue = constrain(personBValue, minValue, maxValue);
  dragTarget = null;
  isDragging = false;
  resetAnimation();
}

// ==============================
// Interaction
// ==============================
function mousePressed() {
  // NEW POSITIONS button
  if (
    mouseX >= newDistBtn.x && mouseX <= newDistBtn.x + newDistBtn.w &&
    mouseY >= newDistBtn.y && mouseY <= newDistBtn.y + newDistBtn.h
  ) {
    newRound();
    return;
  }

  // CALCULATE button (idle only)
  if (animState === "idle" && Math.abs(personAValue - personBValue) > 0) {
    if (
      mouseX >= calcBtn.x && mouseX <= calcBtn.x + calcBtn.w &&
      mouseY >= calcBtn.y && mouseY <= calcBtn.y + calcBtn.h
    ) {
      startAnimation();
      return;
    }
  }

  // RESET button (step4 only)
  if (animState === "step4") {
    if (
      mouseX >= resetBtn.x && mouseX <= resetBtn.x + resetBtn.w &&
      mouseY >= resetBtn.y && mouseY <= resetBtn.y + resetBtn.h
    ) {
      resetAnimation();
      return;
    }
  }

  // Dragging people (idle only)
  if (animState !== "idle") return;

  const centerY = axisY - profileRadius - arrowToAxisLength - 6;
  const ax = valueToX(personAValue);
  const bx = valueToX(personBValue);

  if (dist(mouseX, mouseY, ax, centerY) <= personHitRadius) {
    dragTarget = "A"; isDragging = true; return;
  }
  if (dist(mouseX, mouseY, bx, centerY) <= personHitRadius) {
    dragTarget = "B"; isDragging = true;
  }
}

function mouseDragged() {
  if (animState !== "idle") return;
  if (!isDragging || !dragTarget) return;
  const v = round(xToValue(mouseX));
  const clamped = constrain(v, minValue, maxValue);
  if (dragTarget === "A") personAValue = clamped;
  else personBValue = clamped;
}

function mouseReleased() {
  isDragging = false;
  dragTarget = null;
}

// ==============================
// Helpers
// ==============================
function valueToX(v) {
  return map(v, minValue, maxValue, axisStartX, axisEndX);
}

function xToValue(x) {
  return map(x, axisStartX, axisEndX, minValue, maxValue);
}
