/* jshint esversion: 8 */
// Distance Between Rippa and Eon – number line
// - Both profiles (Eon and Rippa) are movable
// - Distance = |position A − position B|
// - "NEW POSITIONS" gives random positions for both

// ==============================
// Config
// ==============================
const minValue = -20;
const maxValue = 20;
const tickSpacing = 1;
const labelInterval = 5;
const personHitRadius = 40;

// ==============================
// State
// ==============================
let initialized = false;
let axisY;
let axisStartX;
let axisEndX;

let personAValue = -5;  // Eon
let personBValue = 7;   // Rippa
let dragTarget = null;  // "A" | "B" | null
let isDragging = false;

const newDistBtn = { x: 0, y: 0, w: 160, h: 40 };

let imgKid, imgMe;
const profileRadius = 32;
const profileOutlineWidth = 3;
const arrowToAxisLength = 28;
const gapAboveProfiles = 22;
const labelAboveLineGap = 16;
const labelPersonA = "Eon";
const labelPersonB = "Rippa";

// Inline fallbacks for p5canvas / when assets-data.js is not loaded
// (kid.png = Eon, me.png = Rippa) – regenerated by build-assets-data.js
// ASSET_FALLBACKS_START
const ASSET_ME_DATA_FALLBACK = "data:image/png;base64,";
const ASSET_KID_DATA_FALLBACK = "data:image/png;base64,";
// ASSET_FALLBACKS_END

// ==============================
// Setup (async image loading)
// ==============================
async function setup() {
  createCanvas(900, 500);
  textFont("Arial");
  textAlign(CENTER, CENTER);
  updateLayout();
  
  // Use inline Base64 fallbacks (works for p5canvas and file:// protocols)
  var meData = ASSET_ME_DATA_FALLBACK;
  var kidData = ASSET_KID_DATA_FALLBACK;
  
  // Try window globals from assets-data.js (works for web server)
  if (typeof window !== "undefined" && window.ASSET_ME_DATA) {
    meData = window.ASSET_ME_DATA;
  }
  if (typeof window !== "undefined" && window.ASSET_KID_DATA) {
    kidData = window.ASSET_KID_DATA;
  }
  
  var base = "";
  try { base = new URL("./", window.location.href).href; } catch (e) {}
  
  imgMe = await loadImage(meData || (base + "assets/me.png"));
  imgKid = await loadImage(kidData || (base + "assets/kid.png"));
  
  newRound();
  initialized = true;
}

function draw() {
  if (!initialized) {
    if (typeof createCanvas === "function") createCanvas(900, 500);
    if (typeof textFont === "function") textFont("Arial");
    if (typeof textAlign === "function") textAlign(CENTER, CENTER);
    updateLayout();
    newRound();
    initialized = true;
  } else {
    updateLayout();
  }

  background(255);

  drawInstructions();
  drawNumberLine();
  drawPersonA();
  drawPersonB();
  drawFeedback();
  drawNewDistanceButton();
}

function updateLayout() {
  axisY = height * 0.5;
  axisStartX = 80;
  axisEndX = width - 80;
  newDistBtn.x = width - newDistBtn.w - 24;
  newDistBtn.y = height - 56;
}

// ==============================
// Instructions
// ==============================
function drawInstructions() {
  noStroke();
  fill(40);
  textSize(20);
  textStyle(BOLD);
  text("Distance Between Rippa and Eon", width / 2, 32);
  textStyle(NORMAL);
  textSize(16);
  fill(60);
  text("Drag either person. The distance between them is |position of one \u2212 position of the other|.", width / 2, 58);
}

// ==============================
// Number line
// ==============================
function drawNumberLine() {
  stroke(40);
  strokeWeight(3);
  line(axisStartX, axisY, axisEndX, axisY);
  drawArrowHead(axisStartX, axisY, -1);
  drawArrowHead(axisEndX, axisY, 1);

  // Ticks every 1, labels every 5
  for (let v = minValue; v <= maxValue; v++) {
    const x = valueToX(v);
    const isLabel = v % labelInterval === 0;
    stroke(30);
    strokeWeight(isLabel ? 3 : 2);
    const h = isLabel ? 18 : 10;
    line(x, axisY - h / 2, x, axisY + h);
    if (isLabel) {
      noStroke();
      fill(60);
      textSize(14);
      text(v, x, axisY + 36);
    }
  }
}

function drawArrowHead(x, y, dir) {
  push();
  translate(x, y);
  noStroke();
  fill(40);
  const size = 10;
  triangle(dir * 0, 0, dir * -size, -size / 1.2, dir * -size, size / 1.2);
  pop();
}

// ==============================
// Person B (Rippa) – movable profile
// ==============================
function drawPersonB() {
  const px = valueToX(personBValue);
  const centerY = axisY - profileRadius - arrowToAxisLength - 8;

  if (dragTarget === "B") {
    noFill();
    stroke(80, 100, 160);
    strokeWeight(3);
    ellipse(px, centerY, profileRadius * 2 + 10, profileRadius * 2 + 10);
  }
  drawProfileWithArrow(px, centerY, imgMe, true);

  const labelTopY = centerY - profileRadius - gapAboveProfiles - labelAboveLineGap;
  noStroke();
  fill(40);
  textSize(14);
  text(personBValue, px, labelTopY);
  fill(60);
  textSize(14);
  text(labelPersonB, px, axisY + 52);
}

// Draw a circular profile image with outline and arrow pointing down to axis
function drawProfileWithArrow(centerX, centerY, img, isDraggable) {
  const r = profileRadius;
  if (img && img.width > 0) {
    push();
    translate(centerX, centerY);
    drawingContext.save();
    drawingContext.beginPath();
    drawingContext.arc(0, 0, r, 0, TWO_PI);
    drawingContext.clip();
    imageMode(CENTER);
    image(img, 0, 0, r * 2, r * 2);
    drawingContext.restore();
    pop();
  } else {
    fill(200);
    noStroke();
    ellipse(centerX, centerY, r * 2, r * 2);
  }
  // Outline (professional ring)
  noFill();
  stroke(60, 60, 80);
  strokeWeight(profileOutlineWidth);
  ellipse(centerX, centerY, r * 2, r * 2);
  noStroke();
  // Arrow from below circle down to axis
  const arrowTopY = centerY + r;
  const arrowBottomY = axisY;
  const arrowTipY = axisY - 6;
  stroke(50);
  strokeWeight(2);
  line(centerX, arrowTopY, centerX, arrowTipY);
  noStroke();
  fill(50);
  triangle(centerX - 6, arrowTipY, centerX + 6, arrowTipY, centerX, arrowBottomY);
}

// ==============================
// Person A (Eon) – movable profile
// ==============================
function drawPersonA() {
  const px = valueToX(personAValue);
  const centerY = axisY - profileRadius - arrowToAxisLength - 8;

  if (dragTarget === "A") {
    noFill();
    stroke(140, 80, 180);
    strokeWeight(3);
    ellipse(px, centerY, profileRadius * 2 + 10, profileRadius * 2 + 10);
  }
  drawProfileWithArrow(px, centerY, imgKid, true);

  const labelTopY = centerY - profileRadius - gapAboveProfiles - labelAboveLineGap;
  fill(60, 40, 120);
  textSize(14);
  text(personAValue, px, labelTopY);
  fill(60);
  textSize(14);
  text(labelPersonA, px, axisY + 52);
}

// ==============================
// Feedback: distance between the two people
// ==============================
function drawFeedback() {
  const distUnits = Math.abs(personAValue - personBValue);
  const profileCenterY = axisY - profileRadius - arrowToAxisLength - 8;
  const personAX = valueToX(personAValue);
  const personBX = valueToX(personBValue);
  const distanceLineY = profileCenterY - profileRadius - gapAboveProfiles;
  const leftX = Math.min(personAX, personBX);
  const rightX = Math.max(personAX, personBX);
  const profileTopY = profileCenterY - profileRadius;

  if (distUnits === 0) {
    noStroke();
    fill(40, 120, 60);
    textSize(22);
    textStyle(BOLD);
    text("Same position! Distance is 0.", width / 2, axisY + 95);
    textStyle(NORMAL);
    return;
  }

  // Ticks from each profile up to the distance line
  stroke(34, 100, 50);
  strokeWeight(2);
  line(personAX, profileTopY, personAX, distanceLineY);
  line(personBX, profileTopY, personBX, distanceLineY);

  // Horizontal distance line
  stroke(34, 100, 50);
  strokeWeight(4);
  line(leftX, distanceLineY, rightX, distanceLineY);
  noStroke();
  fill(34, 100, 50);
  const arrowTowardB = personAValue < personBValue;
  if (arrowTowardB) {
    triangle(rightX - 12, distanceLineY, rightX, distanceLineY - 8, rightX, distanceLineY + 8);
  } else {
    triangle(leftX + 12, distanceLineY, leftX, distanceLineY - 8, leftX, distanceLineY + 8);
  }

  noStroke();
  fill(20, 80, 40);
  textSize(16);
  textStyle(BOLD);
  text(distUnits, (leftX + rightX) / 2, distanceLineY - 14);
  textStyle(NORMAL);

  noStroke();
  fill(40);
  textSize(16);
  const msgY = axisY + 78;
  text(`Distance between Rippa and Eon is ${distUnits} units.`, width / 2, msgY);
  text(`| ${personAValue} − ${personBValue} | = ${distUnits}`, width / 2, msgY + 22);
  textStyle(BOLD);
  text(`| ${personAValue} − (${personBValue}) | = ${distUnits}`, width / 2, msgY + 46);
  textStyle(NORMAL);
}

// ==============================
// NEW POSITIONS button
// ==============================
function drawNewDistanceButton() {
  const hover =
    mouseX >= newDistBtn.x &&
    mouseX <= newDistBtn.x + newDistBtn.w &&
    mouseY >= newDistBtn.y &&
    mouseY <= newDistBtn.y + newDistBtn.h;

  stroke(80, 40, 120);
  strokeWeight(2);
  fill(hover ? 200 : 230, 220, 245);
  rect(newDistBtn.x, newDistBtn.y, newDistBtn.w, newDistBtn.h, 8);

  noStroke();
  fill(80, 40, 120);
  textSize(16);
  textStyle(BOLD);
  text("NEW POSITIONS", newDistBtn.x + newDistBtn.w / 2, newDistBtn.y + newDistBtn.h / 2);
  textStyle(NORMAL);
}

// ==============================
// New round: random non-zero position
// ==============================
function newRound() {
  personAValue = floor(random(minValue, maxValue + 1));
  personBValue = floor(random(minValue, maxValue + 1));
  personAValue = constrain(personAValue, minValue, maxValue);
  personBValue = constrain(personBValue, minValue, maxValue);
  dragTarget = null;
  isDragging = false;
}

// ==============================
// Interaction
// ==============================
function mousePressed() {
  if (
    mouseX >= newDistBtn.x &&
    mouseX <= newDistBtn.x + newDistBtn.w &&
    mouseY >= newDistBtn.y &&
    mouseY <= newDistBtn.y + newDistBtn.h
  ) {
    newRound();
    return;
  }

  const centerY = axisY - profileRadius - arrowToAxisLength - 8;
  const ax = valueToX(personAValue);
  const bx = valueToX(personBValue);

  if (dist(mouseX, mouseY, ax, centerY) <= personHitRadius) {
    dragTarget = "A";
    isDragging = true;
    return;
  }
  if (dist(mouseX, mouseY, bx, centerY) <= personHitRadius) {
    dragTarget = "B";
    isDragging = true;
  }
}

function mouseDragged() {
  if (!isDragging || !dragTarget) return;
  const v = round(xToValue(mouseX));
  const clamped = constrain(v, minValue, maxValue);
  if (dragTarget === "A") personAValue = clamped;
  else personBValue = clamped;
}

function mouseReleased() {
  isDragging = false;
  dragTarget = null;
}

// ==============================
// Helpers
// ==============================
function valueToX(v) {
  return map(v, minValue, maxValue, axisStartX, axisEndX);
}

function xToValue(x) {
  return map(x, axisStartX, axisEndX, minValue, maxValue);
}
